pe":"string"},{"internalType":"string","name":"description","type":"string"},{"internalType":"uint256","name":"price","type":"uint256"},{"internalType":"string","name":"image","type":"string"},{"internalType":"uint256","name":"createdAt","type":"uint256"},{"internalType":"address","name":"buyer","type":"address"},{"internalType":"bool","name":"hasBuyer","type":"bool"},{"internalType":"contract Escrow","name":"escrow","type":"address"}],"internalType":"struct Item.ItemStruct[]","name":"","type":"tuple[]"}],"stateMutability":"view","type":"function"}],"devdoc":{"kind":"dev","methods":{},"version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"contracts/Item.sol":"Item"},"evmVersion":"paris","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":false,"runs":200},"remappings":[]},"sources":{"contracts/Escrow.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;
import "./Item.sol";

contract Escrow {
    Item public itemContract;

    struct EscrowStruct {
        address buyer;
        address seller;
        uint itemId;
        uint price;
        bool isCompleted;
    }

    mapping(uint => EscrowStruct) public escrows;
    uint public escrowCount;

    function createEscrow(address _buyer, address _seller, uint _itemId, uint _price) external returns (address) {
        escrowCount++;
        escrows[escrowCount] = EscrowStruct(_buyer, _seller, _itemId, _price, false);
        return address(this);
    }

    function confirmDelivery(uint _escrowId) external {
        require(escrows[_escrowId].buyer == msg.sender, "Only buyer can confirm");
        require(!escrows[_escrowId].isCompleted, "Escrow already completed");

        escrows[_escrowId].isCompleted = true;
        payable(escrows[_escrowId].seller).transfer(escrows[_escrowId].price);
    }

    function refundBuyer(uint _escrowId) external {
        // require(msg.sender == itemContract.buyer, "Only buyer can refund");
        require(!escrows[_escrowId].isCompleted, "Escrow already completed");

        escrows[_escrowId].isCompleted = true;
        payable(escrows[_escrowId].buyer).transfer(escrows[_escrowId].price);
    }
}

","keccak256":"0x8f6046c57feea5b6916ab40224eead25d092bdbc1ab75c82c73bb45635d4ce6f","license":"MIT"},"contracts/Item.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;
import "./Escrow.sol";
import "hardhat/console.sol";

contract Item {
    struct ItemStruct {
        uint itemId;
        string title;
        string description;
        uint price;
        string image;
        uint createdAt;
        address buyer;
        bool hasBuyer;
        Escrow escrow;
    }

    mapping(address => ItemStruct[]) private items;
    uint public itemsCount;
    address[] private sellers;

    // Escrow Contract instance
    // Escrow public escrowContract;

    // constructor() {
    //     escrowContract = new Escrow(address(this));
    // }

    function addItem(
        string memory _title,
        string memory _description,
        uint _price,
        string memory _image
    ) external {
        console.log("Price is: %s", _price);
        if (items[msg.sender].length == 0) {
            sellers.push(msg.sender);
        }
        itemsCount++;
        items[msg.sender].push(ItemStruct(
            itemsCount,
            _title,
            _description,
            _price,
            _image,
            block.timestamp,
            address(0),
            false,
            Escrow(address(0))
        ));
    }

    function editItem(
        uint _itemId,
        string memory _title,
        string memory _description,
        uint _price,
        string memory _image
    ) external {
        // Check if the item belongs to the msg.sender
        require(_itemId > 0 && _itemId <= itemsCount, "Invalid item ID");
        ItemStruct[] storage userItems = items[msg.sender];
        bool itemFound = false;
        for (uint i = 0; i < userItems.length; i++) {
            if (userItems[i].itemId == _itemId) {
                itemFound = true;
                require(!userItems[i].hasBuyer, "Item already sold");
                userItems[i].title = _title;
                userItems[i].description = _description;
                userItems[i].price = _price;
                userItems[i].image = _image;
                break;
            }
        }
        require(itemFound, "Item does not belong to sender");
    }

    function deleteItem(uint _itemId) external {
        require(_itemId < items[msg.sender].length, "Item does not exist");
        ItemStruct storage item = items[msg.sender][_itemId];
        require(!item.hasBuyer, "Item already sold");

        uint lastIndex = items[msg.sender].length - 1;
        if (_itemId != lastIndex) {
            items[msg.sender][_itemId] = items[msg.sender][lastIndex];
            items[msg.sender][_itemId].itemId = _itemId;
        }
        items[msg.sender].pop();
    }

    function retrieveUserItems() external view returns (ItemStruct[] memory) {
        return items[msg.sender];
    }

    function browseItems() external view returns (ItemStruct[] memory) {
        uint totalItems = 0;
        uint currentIndex = 0;

        // Calculate total items excluding the user's items
        for (uint s = 0; s < sellers.length; s++) {
            if (sellers[s] != msg.sender) {
                totalItems += items[sellers[s]].length;
            }
        }

        // Create an array to hold all items
        ItemStruct[] memory allItems = new ItemStruct[](totalItems);

        // Populate the array with items not created by the user
        for (uint s = 0; s < sellers.length; s++) {
            if (sellers[s] != msg.sender) {
                ItemStruct[] storage sellerItems = items[sellers[s]];
                for (uint i = 0; i < sellerItems.length; i++) {
                    allItems[currentIndex] = sellerItems[i];
                    currentIndex++;
                }
            }
        }

        console.log("Total Items: ", totalItems);
        console.log("Current Index: ", currentIndex);

        return allItems;
    }

}

